_paterno = date[x].apellido_paterno;                        var ciudad = date[x].ciudad;                        var cp = date[x].cp;                        var curp = date[x].curp;                        var direccion = date[x].direccion;                        var edad = date[x].edad;                        var email = date[x].email;                        var email2 = date[x].email2;                        var emergencias = date[x].emergencias;                        var escegreso = date[x].escegreso;                        var estatus = date[x].estatus;                        var fecha = date[x].fecha;                        var fecha_nacimiento = date[x].fecha_nacimiento;                        var fechaegreso = date[x].fechaegreso;                        var genero = date[x].genero;                        var idigenerales = date[x].idigenerales;                        var infoadicional = date[x].infoadicional;                        var interes = date[x].interes;                        var movil = date[x].movil;                        var municipio = date[x].municipio;                        var nivelegreso = date[x].nivelegreso;                        var nombre = date[x].nombre;                        var nss = date[x].nss;                        var pais = date[x].pais;                        var rfc = date[x].rfc;                        var telefono = date[x].telefono;                        var tiposangre = date[x].tiposangre;                        var turno = date[x].turno;                    }                    $("#alergias").val(alergias);                    $("#apellido_materno").val(apellido_materno);                    $("#apellido_paterno").val(apellido_paterno);                    $("#locality").val(ciudad);                    $("#postal_code").val(cp);                    $("#curp").val(curp);                    $("#route").val(direccion);                    $("#edad").val(edad);                    $("#email").val(email);                    $("#email2").val(email2);                    $("#emergencias").val(emergencias);                    $("#escegreso").val(escegreso);                    $("#estatus").val(estatus);                    $("#fecha").val(fecha);                    $("#fecha_nacimiento").val(fecha_nacimiento);                    $("#fechaegreso").val(fechaegreso);                    $("#genero").val(genero);                    $("#idigenerales2").val(idigenerales);                    $("#infoadicional").val(infoadicional);                    $("#interes").val(interes);                    $("#movil").val(movil);                    $("#administrative_area_level_1").val(municipio);                    $("#nivelegreso").val(nivelegreso);                    $("#nombre").val(nombre);                    $("#nss").val(nss);                    $("#country").val(pais);                    $("#rfc").val(rfc);                    $("#telefono").val(telefono);                    $("#tiposangre").val(tiposangre);                    $("#turno").val(turno);                    $("#cabecera").append(nombre + " " + apellido_paterno + " " + apellido_materno);                                    },                error: function (jqXHR, textStatus, errorThrown) {                    console.log(jqXHR);                    console.log(textStatus);                    console.log(errorThrown);                   // alert("No fue posible conectar con el servidor");                }            });        }    </script>    <script>        // This sample uses the Autocomplete widget to help the user select a        // place, then it retrieves the address components associated with that        // place, and then it populates the form fields with those details.        // This sample requires the Places library. Include the libraries=places        // parameter when you first load the API. For example:        // <script        // src="https://maps.googleapis.com/maps/api/js?key=YOUR_API_KEY&libraries=places">        var placeSearch, autocomplete;        var componentForm = {            street_number: 'short_name',            route: 'long_name',            locality: 'long_name',            administrative_area_level_1: 'short_name',            country: 'long_name',            postal_code: 'short_name'        };        function initAutocomplete() {            // Create the autocomplete object, restricting the search predictions to            // geographical location types.            autocomplete = new google.maps.places.Autocomplete(                    document.getElementById('autocomplete'), {types: ['geocode']});            // Avoid paying for data that you don't need by restricting the set of            // place fields that are returned to just the address components.            autocomplete.setFields('address_components');            // When the user selects an address from the drop-down, populate the            // address fields in the form.            autocomplete.addListener('place_changed', fillInAddress);        }        function fillInAddress() {            // Get the place details from the autocomplete object.            var place = autocomplete.getPlace();            for (var component in componentForm) {                document.getElementById(component).value = '';                document.getElementById(component).disabled = false;            }            // Get each component of the address from the place details,            // and then fill-in the corresponding field on the form.            for (var i = 0; i < place.address_components.length; i++) {                var addressType = place.address_components[i].types[0];                if (componentForm[addressType]) {                    var val = place.address_components[i][componentForm[addressType]];                    document.getElementById(addressType).value = val;                }            }        }        // Bias the autocomplete object to the user's geographical location,        // as supplied by the browser's 'navigator.geolocation' object.        function geolocate() {            if (navigator.geolocation) {                navigator.geolocation.getCurrentPosition(function (position) {                    var geolocation = {                        lat: position.coords.latitude,                        lng: position.coords.longitude                    };                    var circle = new google.maps.Circle(                            {center: geolocation, radius: position.coords.accuracy});                    autocomplete.setBounds(circle.getBounds());                });            }        }    </script>    <script src="https://maps.googleapis.com/maps/api/js?key=AIzaSyCABXOLbXlqxpYVeGDggtlghS5DLASUCxU&libraries=places&callback=initAutocomplete"    async defer></script>